mapper.select:
SELECT 

FROM p_classify a

WHERE a.id = #{id}

SELECT 

FROM p_classify a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

		AND a.parent_id = #{parent.id}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.parent_ids = #{parentIds}


ORDER BY a.sort ASC

SELECT 

FROM p_classify a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

ORDER BY a.sort ASC

SELECT a.id, a.parent_id AS "parent.id", a.parent_ids FROM p_classify a
a.del_flag = #{DEL_FLAG_NORMAL} AND a.parent_ids LIKE #{parentIds}
ORDER BY a.sort ASC

mapper.update:
UPDATE p_classify SET 
	parent_id = #{parent.id}, 
	parent_ids = #{parentIds}
WHERE id = #{id}
